import React from 'react';
export default class Counter1 extends React.Component {
  
    constructor(props) {
        super(props);

        this.state = {
            counter : this.props.counter,
            statusBtn: this.props.statusBtn,
            check: this.props.check,
        }
    }
    // Show = () => {
    //     if (this.state.check === 1) {
    //         this.setState({
    //             counter: this.state.counter + 1
    //         })
    //     }

    //     if (this.state.check === 2) {
    //         this.setState({
    //             counter: this.state.counter - 1
    //         })
    //     }
    // }

    changebtn = () => {
        if (this.state.check == true) {
            return this.Up();
        }
        else if (this.state.check == false) {
            return this.Down();
        }
    }
    Up = () => {
        return (
            <button onClick={this.setState({
                                counter: this.state.counter + 1

            })}>Up</button>
            )
    }
    Down = () => {
        return (
            <button onClick={this.setState({
                                counter: this.state.counter - 1

            })}>Down</button>
        )
    } 

    render(){
        return (
            <div className="App">
                <div >{this.state.counter}</div>
                {this.changebtn()}
            </div>
        );
    }
}
