import React from 'react';
export default class Counter1 extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            counter : this.props.counter,
            statusBotton: "UP",
            check: true,
            display
        }
    }
    Show = () => {
        if (this.state.check === true) {
            this.setState({
                counter: this.state.counter + 1
            })
        }

        if (this.state.counter === 5) {
            this.setState({
                statusBotton: "DOWN",
                check: false,
            })
        }
        if (this.state.check === false) {
            this.setState({
                counter: this.state.counter - 1
            })
        }
    }

    render(){
        return (
            <div className="App">
                <div style={display}>{this.state.counter}</div>
                <button onClick={this.Show}>{this.state.statusBotton}</button>
            </div >
        );
    }
}
